import {Observable, of} from 'rxjs';
import { Injectable } from '@angular/core';
import {Setting} from './setting.model';
import {ExhibitService} from '../exhibit/exhibit.service';
import {SettingCategory} from '../category/setting-category.model';
import {ExhibitCategory} from '../category/exhibit-category.model';

@Injectable({
  providedIn: 'root'
})
export class SettingService {

  private settingList = [
    {
      id: 1,
      name: '83-84 Championship',
      description: 'The 1983â€“84 NBA season was the 38th season of the National Basketball Association. The season ended with the Boston Celtics winning the NBA Championship, beating the Los Angeles Lakers 4 games to 3 for the second time since 1969 in the NBA Finals.',
      imageURL: 'data:image/jpeg;base64,',
      category: {id: 1, name: 'Boston Celtics'},
      exhibits: [],
      averageReviewValue: 0,
      tourTime: 0,
      price: 0
    },
    {
      id: 2, name: '07-08 Championship',
      description: 'The 2007â€“08 NBA season was the 62nd season of the National Basketball Association. The Boston Celtics defeated the Los Angeles Lakers 131â€“92 to win the 2008 NBA Finals, four games to two. The 2007 NBA draft was held on June 28, 2007 and Greg Oden was selected first overall by the Portland Trail Blazers. However, he missed the entire season due to knee surgery.',
      imageURL: 'data:image/jpeg;base64,',
      category: {id: 1, name: 'Boston Celtics'},
      exhibits: [],
      averageReviewValue: 0,
      tourTime: 0,
      price: 0
    }
  ];

  constructor(private exhibitService: ExhibitService) { }

  getAll(): Observable<any> {
    return of(this.settingList);
  }

  getById(id: number): Observable<any> {
    let setting: Setting = this.settingList.find(s => s.id === id);
    setting.exhibits = this.exhibitService.getBySettingId(setting.id);
    let notReviewed = 0;
    setting.averageReviewValue =
      setting.exhibits.reduce((total, current) => {
        if (current.averageReviewValue === 0) {
          notReviewed++;
        }
        return total + current.averageReviewValue;
      }, 0) / (setting.exhibits.length - notReviewed);
    if (isNaN(setting.averageReviewValue))
      setting.averageReviewValue = 0;
    setting.tourTime = setting.exhibits.reduce((total, current) => total + current.tourTime, 0);
    setting.price = setting.exhibits.reduce((total, current) => total + current.price, 0);
    return of(setting);
  }

  getBySearchCriteria(value: string): Observable<any> {
    return of(
      this.settingList.filter(s => s.name.toLowerCase().includes(value.toLowerCase())
        || s.description.toLowerCase().includes(value.toLowerCase()))
    );
  }

  getAllByFilters(settingCategories: SettingCategory[], exhibitCategories: ExhibitCategory[], maximumPrice: number,
                  rating: number, numberOfExhibits: number, minimalTourTime: number): Observable<any> {
    let result: Setting[] = [];
    this.settingList.filter(s => {
      this.getById(s.id).subscribe(r => {
        if (settingCategories.map(c => c.id).includes(r.category.id) &&
          maximumPrice >= r.price &&
          rating <= r.averageReviewValue &&
          numberOfExhibits <= r.exhibits.length &&
          minimalTourTime <= r.tourTime) {
          const size: number = r.exhibits.filter(e => exhibitCategories.map(c => c.id).includes(e.category.id)).length;
          if (size > 0)
            result.push(s);
        }
      });
    });
    return of(result);
  }

}
